import * as types from "../types";

/**
 *
 * @param {text_color, bg_color, msg} msg
 */
export const setAlertAction = (msg) => {
	return {
		type: types.SET_ALERT,
		msg,
	};
};

export const resetAlertAction = () => {
	return {
		type: types.RESET_ALERT,
	};
};
