import React, { useEffect } from "react";
import { Route, Switch, Redirect } from "react-router-dom";
import { auth, firebase } from "./config/firebase";

//-----router

import LandingPage from "./components/Pages/LandingPage/LandingPage";
import PageNotFound from "./components/Pages/PageNotFound";

import Layout from "./components/Pages/Dashboards/Layout";
import Login from "./components/Pages/Auth/Login/Login";
import Signup from "./components/Pages/Auth/Signup/Signup";
import UserRoute from "./routes/UserRoute";
import RegisterForm from "./components/Pages/Auth/Signup/RegisterForm";
import { connect } from "react-redux";
import { logoutUserAction, userLoggedIn } from "./redux/actions/userActions";

function App(props) {
	useEffect(() => {
		const unsubscibe = auth.onAuthStateChanged((authUser) => {
			const usersRef = firebase.firestore().collection("users");
			if (authUser) {
				console.log("here");
				usersRef
					.doc(authUser.uid)
					.set({ ...authUser, id: authUser.uid })
					.then((data) => {
						console.log(data);
						props.dispatch(userLoggedIn(authUser));
					})
					.catch((error) => {
						console.log(error);
						props.dispatch(logoutUserAction());
					});
			} else {
				props.dispatch(logoutUserAction());
			}
		});
		return () => {
			unsubscibe();
		};
	}, []);
	return (
		<div>
			<Switch>
				<Route exact path="/" render={() => <Redirect to="/home" />} />
				<Route path="/login" component={Login} />
				<Route path="/signup" component={Signup} />
				<Route path="/home" component={LandingPage} />
				<UserRoute path="/register" component={RegisterForm} />
				<Route path="/buyer" component={Layout} />
				<Route path="/**" component={PageNotFound} />
			</Switch>
		</div>
	);
}
const mapStateToProps = ({ user }) => ({ user });

export default connect(mapStateToProps)(App);
