import React, { useContext, useState } from "react";
// import { SidebarContext } from "../context/SidebarContext";
import {
	SearchIcon,
	MoonIcon,
	SunIcon,
	BellIcon,
	MenuIcon,
	OutlinePersonIcon,
	OutlineCogIcon,
	OutlineLogoutIcon,
	ActiIcon,
} from "../../../assets/icons";

// import {
// 	Avatar,
// 	Badge,
// 	Input,
// 	Dropdown,
// 	DropdownItem,
// 	WindmillContext,
// } from "@windmill/react-ui";

function Header() {
	const { mode, toggleMode } = useState(false);
	const { toggleSidebar } = useState(false);

	const [isNotificationsMenuOpen, setIsNotificationsMenuOpen] = useState(false);
	const [isProfileMenuOpen, setIsProfileMenuOpen] = useState(false);

	function handleNotificationsClick() {
		setIsNotificationsMenuOpen(!isNotificationsMenuOpen);
	}

	function handleProfileClick() {
		setIsProfileMenuOpen(!isProfileMenuOpen);
	}

	return (
		<header className="z-40 py-4 bg-white shadow-bottom dark:bg-gray-800">
			<div className="container flex items-center justify-between h-full px-3 mx-auto text-grey-900 dark:text-purple-300">
				<ActiIcon className="fill-current h-10"></ActiIcon>
				{/* <!-- Mobile hamburger --> */}
				<button
					className="p-1 mr-5 -ml-1 rounded-md lg:hidden focus:outline-none focus:shadow-outline-aucti"
					onClick={toggleSidebar}
					aria-label="Menu"
				>
					<MenuIcon className="w-6 h-6" aria-hidden="true" />
				</button>
				{/* <!-- Search input --> */}
				<div className="flex justify-center flex-1 lg:mr-32">
					<div className="relative w-full max-w-xl mr-6 focus-within:text-gray-400">
						<div className="absolute inset-y-0 flex items-center pl-2">
							<SearchIcon className="w-4 h-4" aria-hidden="true" />
						</div>
						<input
							className="pl-8 text-gray-700"
							placeholder="Search for products"
							aria-label="Search"
						/>
					</div>
				</div>
				<ul className="flex items-center flex-shrink-0 space-x-6">
					{/* <!-- Theme toggler --> */}
					<li className="flex">
						<button
							className="rounded-md focus:outline-none focus:shadow-outline-purple"
							onClick={toggleMode}
							aria-label="Toggle color mode"
						>
							{mode === "dark" ? (
								<SunIcon className="w-5 h-5" aria-hidden="true" />
							) : (
								<MoonIcon className="w-5 h-5" aria-hidden="true" />
							)}
						</button>
					</li>
					{/* <!-- Notifications menu --> */}
					<li className="relative">
						<button
							className=" align-middle rounded-md focus:shadow-outline hover:bg-auctiLight"
							onClick={() => console.log("click")}
							// aria-label="Notifications"
							// aria-haspopup="true"
						>
							<BellIcon className="w-5 h-5" aria-hidden="true" />
							{/* <!-- Notification badge --> */}
							<span
								aria-hidden="true"
								className="absolute top-0 right-0 inline-block w-3 h-3 transform translate-x-1 -translate-y-1 bg-red-600 border-2 border-white rounded-full dark:border-gray-800"
							></span>
						</button>

						{/* <Dropdown
							align="right"
							isOpen={isNotificationsMenuOpen}
							onClose={() => setIsNotificationsMenuOpen(false)}
						>
							<DropdownItem tag="a" href="#" className="justify-between">
								<span>Messages</span>
								<Badge type="danger">13</Badge>
							</DropdownItem>
							<DropdownItem tag="a" href="#" className="justify-between">
								<span>Sales</span>
								<Badge type="danger">2</Badge>
							</DropdownItem>
							<DropdownItem onClick={() => alert("Alerts!")}>
								<span>Alerts</span>
							</DropdownItem>
						</Dropdown> */}
					</li>
					{/* <!-- Profile menu --> */}
					<li className="relative">
						<button
							className="rounded-full focus:shadow-outline-auctiLight focus:outline-none"
							onClick={() => console.log("profilepicture click")}
							// aria-label="Account"
							// aria-haspopup="true"
						>
							<img
								className="inline object-cover w-12 h-12 mr-2 rounded-full"
								src="https://images.unsplash.com/photo-1502378735452-bc7d86632805?ixlib=rb-0.3.5&q=80&fm=jpg&crop=entropy&cs=tinysrgb&w=200&fit=max&s=aa3a807e1bbdfd4364d1f449eaa96d82"
								alt="profile-picture"
								// aria-hidden="true"
							/>
						</button>
						{/* <Dropdown
							align="right"
							isOpen={isProfileMenuOpen}
							onClose={() => setIsProfileMenuOpen(false)}
						>
							<DropdownItem tag="a" href="#">
								<OutlinePersonIcon
									className="w-4 h-4 mr-3"
									aria-hidden="true"
								/>
								<span>Profile</span>
							</DropdownItem>
							<DropdownItem tag="a" href="#">
								<OutlineCogIcon className="w-4 h-4 mr-3" aria-hidden="true" />
								<span>Settings</span>
							</DropdownItem>
							<DropdownItem onClick={() => alert("Log out!")}>
								<OutlineLogoutIcon
									className="w-4 h-4 mr-3"
									aria-hidden="true"
								/>
								<span>Log out</span>
							</DropdownItem>
						</Dropdown> */}
					</li>
				</ul>
			</div>
		</header>
	);
}

export default Header;
